CREATE TABLE "public"."movies" ("id" int GENERATED BY DEFAULT AS IDENTITY NOT NULL, "title" text NOT NULL, "duration_second" integer NOT NULL, "description" text NOT NULL, "published_at" date NOT NULL, "director" integer NOT NULL, "genra" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("director") REFERENCES "public"."casts"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("genra") REFERENCES "public"."genra"("name") ON UPDATE restrict ON DELETE restrict, UNIQUE ("title"), CONSTRAINT "duration_must_be_greater_than_15_minutes" CHECK (duration_second >= 900
));COMMENT ON TABLE "public"."movies" IS E'the movie collections';
