CREATE TABLE "public"."vending_machine_to_items" ("id" bigserial NOT NULL, "machine_id" uuid NOT NULL, "item_id" integer NOT NULL, "amount" Integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("machine_id") REFERENCES "public"."vending_machine"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("item_id") REFERENCES "public"."vending_machine_items"("id") ON UPDATE restrict ON DELETE cascade, UNIQUE ("id"), CONSTRAINT "greater_than_zero" CHECK (amount >= 0));
